echo "  kill $BACKEND_PID $FRONTEND_PID"
echo ""
echo "Feature: Sibling Leaderboards & Friendly Competition"
echo "  - Family-only leaderboards with parent controls"
echo "  - Weekly and all-time rankings"
echo "  - Family challenges with bonus rewards"
echo ""
echo "========================================"
